import * as React from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import { CardActionArea, Container, Box } from "@mui/material";
import GradeIcon from "@mui/icons-material/Grade";
import StarBorderIcon from "@mui/icons-material/StarBorder";
export default function ActionAreaCard(item) {
  return (
    <Card sx={{ maxWidth: 345 }}>
      <CardActionArea>
        <CardContent
          sx={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            flexDirection: "column",
          }}
        >
          <img
            src={item.item.image}
            style={{ width: "200px", maxHeight: "280px", padding: "30px" }}
          />
          <Typography gutterBottom variant="h6" sx={{ textAlign: "center" }}>
            {item.item.title}
          </Typography>
          <Typography
            variant="body2"
            color="text.secondary"
            sx={{ textAlign: "center", my: 1 }}
          >
            {item.item.description.substr(0, 130)}...
          </Typography>
          <Box sx={{ my: 1 }}>
            {/* <StarBorderIcon /> */}

            {[1, 2, 3, 4, 5].map((i) => {
              return Math.ceil(item.item.rating.rate) >= i ? (
                <GradeIcon />
              ) : (
                <StarBorderIcon />
              );
            })}
          </Box>
          <Typography gutterBottom variant="subtitle2">
            Price: ${item.item.price}
          </Typography>
        </CardContent>
      </CardActionArea>
    </Card>
  );
}
